settings.liquid:
{% comment %}
  Responsive Product Specifications Table
  Desktop: Table with SVG below (60x60px)
  Mobile: Horizontal scroll table with SVG above
{% endcomment %}

<style>
  .responsive-specs-table {
    width: 100%;
    max-width: 800px;
    margin: 1rem 0;
    word-break: keep-all;
    hyphens: none;
    overflow-wrap: normal;
  }

  /* Desktop table styles */
  .specs-table {
    width: 100%;
    border-collapse: collapse;
    border: 2px ridge #cacace;
    background: #fff;
    word-break: keep-all;
    hyphens: none;
    overflow-wrap: normal;
  }

  .specs-table th,
  .specs-table td {
    border: 2px groove #cacace;
    padding: 1rem;
    text-align: left;
    vertical-align: top;
    word-break: keep-all;
    hyphens: none;
    overflow-wrap: normal;
  }

  .specs-table th {
    background-color: #ededed;
    font-weight: bold;
    color: #072442;
  }

  .specs-table th h6 {
    margin: 0;
    color: #072442;
    font-weight: bold;
  }

  .specs-table td {
    background-color: #ffffff;
  }

  .specs-table ul {
    margin: 0;
    padding-left: 1.2rem;
    color: hsl(211, 81%, 14%);
    word-break: keep-all;
    hyphens: none;
    overflow-wrap: normal;
  }

  .specs-table li {
    margin-bottom: 0.5rem;
  }

  .specs-table li:last-child {
    margin-bottom: 0;
  }

  /* Table scroll container */
  .table-scroll {
    overflow-x: auto;
    -webkit-overflow-scrolling: touch;
  }

  /* Mobile responsive 

    /* Adjust table for mobile scroll */
    .responsive-specs-table {
      font-size: 0.9rem;
    }

    .specs-table th,
    .specs-table td {
      padding: 0.75rem;
      min-width: 120px;
      white-space: nowrap;
    }

    .specs-table ul {
      padding-left: 1rem;
    }

    .specs-table li {
      margin-bottom: 0.25rem;
    }
  }
</style>

<div class="responsive-specs-table">
<!-- Table with horizontal scroll -->
  <div class="table-scroll">
    <table class="specs-table">
